    sc_assert(surface.kindOf(SC.Surface));
    sc_assert(surface.get('supersurface') === null);
    sc_assert(surface === SC.surfaces[surface.__id__]);

    sc_assert(psurface);
    sc_assert(psurface === SC._sc_currentPsurface);
